package Que1;

import java.util.*;

public class listEvenNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=0,i=0;

        

	       Scanner X = new Scanner(System.in);

	         

	       System.out.print("Enter value n : ");

	       n = X.nextInt();

	         

	       for(i=1; i<n; i++)

	       {

	           if(i%2==0)

	               System.out.print(i+" ");

	       }    

	       System.out.println();

	         

	   }

	
}
